root.script:
function onInit()
    if Extension.isEffectBuilderRulesetPluginLoaded and Extension.isEffectBuilderRulesetPluginLoaded() then
        EditorManager.addEffectEditor(
            {
                value="BONUSMOD",
                categoryres="category_misc",
                labelres="effect_bonusmod",
                windowclass="effect_BONUSMOD"
            }
        )
    end
end
